mapper.select:
SELECT * FROM tbl_board
WHERE title like #{keyword} OR
	 content like #{keyword}

mapper.update:
UPDATE tbl_board
SET viewcnt = viewcnt + 1
WHERE bno = #{bno}

mapper.select:
SELECT * FROM tbl_board
WHERE bno = #{bno}
ORDER BY bno

mapper.update:
UPDATE tbl_board
SET title = #{title},
	content = #{content}
WHERE bno = #{bno}
